import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

interface GameProps {
    players: string[]; // List of player names
    strokes: string[][]; // Array of strokes for each hole
    setStrokes: React.Dispatch<React.SetStateAction<string[][]>>; // Function to update strokes
    shotTypes: string[][]; // Array of shot types for each hole
    setShotTypes: React.Dispatch<React.SetStateAction<string[][]>>; // Function to update shot types
    currentHole: number; // Current hole number
    setCurrentHole: React.Dispatch<React.SetStateAction<number>>; // Function to update the current hole
}

const Game: React.FC<GameProps> = ({
    players,
    strokes,
    setStrokes,
    shotTypes,
    setShotTypes,
    currentHole,
    setCurrentHole,
}) => {
    const [selectedPlayers, setSelectedPlayers] = useState<string[]>([]); // Track selected players
    const navigate = useNavigate(); // Initialize useNavigate

    const shotTypeOptions = [
        'Drive',
        'Par 3',
        'Hybrid',
        'Iron',
        'Approach',
        'Chip',
        'Putt',
        'Water Hazard',
    ];

    // Ensure strokes and shotTypes arrays are initialized for the current hole
    useEffect(() => {
        if (!strokes[currentHole - 1]) {
            const updatedStrokes = [...strokes];
            updatedStrokes[currentHole - 1] = [];
            setStrokes(updatedStrokes);
        }

        if (!shotTypes[currentHole - 1]) {
            const updatedShotTypes = [...shotTypes];
            updatedShotTypes[currentHole - 1] = [];
            setShotTypes(updatedShotTypes);
        }
    }, [currentHole, strokes, shotTypes, setStrokes, setShotTypes]);

    const togglePlayerSelection = (playerName: string) => {
        setSelectedPlayers((prev) =>
            prev.includes(playerName)
                ? prev.filter((name) => name !== playerName) // Deselect player
                : [...prev, playerName] // Select player
        );
    };

    const assignPlayersToStroke = (strokeIndex: number) => {
        const updatedStrokes = [...strokes];
        const currentStroke = updatedStrokes[currentHole - 1] || [];

        // Assign all selected players to the stroke
        selectedPlayers.forEach((player) => {
            if (!currentStroke.includes(player)) {
                currentStroke[strokeIndex] = currentStroke[strokeIndex]
                    ? `${currentStroke[strokeIndex]}, ${player}`
                    : player;
            }
        });

        updatedStrokes[currentHole - 1] = currentStroke;
        setStrokes(updatedStrokes);

        // Clear selected players after assignment
        setSelectedPlayers([]);
    };

    const removeStroke = (strokeIndex: number) => {
        const updatedStrokes = [...strokes];
        const updatedShotTypes = [...shotTypes];

        if (updatedStrokes[currentHole - 1]) {
            updatedStrokes[currentHole - 1].splice(strokeIndex, 1); // Remove the stroke
        }
        if (updatedShotTypes[currentHole - 1]) {
            updatedShotTypes[currentHole - 1].splice(strokeIndex, 1); // Remove the shot type
        }

        setStrokes(updatedStrokes);
        setShotTypes(updatedShotTypes);
    };

    const selectShotType = (strokeIndex: number, shotType: string) => {
        const updatedShotTypes = [...shotTypes];
        updatedShotTypes[currentHole - 1][strokeIndex] = shotType; // Set the shot type for the stroke
        setShotTypes(updatedShotTypes);
    };

    const addStroke = () => {
        const updatedStrokes = [...strokes];
        const updatedShotTypes = [...shotTypes];

        if (!updatedStrokes[currentHole - 1]) {
            updatedStrokes[currentHole - 1] = [];
        }
        if (!updatedShotTypes[currentHole - 1]) {
            updatedShotTypes[currentHole - 1] = [];
        }

        updatedStrokes[currentHole - 1].push(''); // Add a new stroke for the current hole
        updatedShotTypes[currentHole - 1].push(''); // Add a new shot type for the current hole

        setStrokes(updatedStrokes);
        setShotTypes(updatedShotTypes);
    };

    // Example of goToLeaderboard implementation
    const goToLeaderboard = () => {
        console.log('Navigating to leaderboard...');
        navigate('/leaderboard'); // Replace with your actual navigation logic
    };

    return (
        <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Hole {currentHole}</h1>
            <div style={{ overflowX: 'auto', marginBottom: '20px' }}>
                <table
                    style={{
                        margin: '0 auto',
                        borderCollapse: 'collapse',
                        width: '100%',
                        maxWidth: '100%',
                        backgroundColor: '#ffffff',
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    }}
                >
                    <thead>
                        <tr style={{ backgroundColor: '#1976d2', color: 'white' }}>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Stroke</th>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Player</th>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Actions</th>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Shot Type</th>
                        </tr>
                    </thead>
                    <tbody>
                        {strokes[currentHole - 1]?.length > 0 ? (
                            strokes[currentHole - 1].map((stroke: string, index: number) => (
                                <tr key={index}>
                                    <td style={{ padding: '10px', border: '1px solid #ccc', whiteSpace: 'nowrap' }}>
                                        Stroke {index + 1}{' '}
                                        <span
                                            onClick={() => removeStroke(index)}
                                            style={{
                                                cursor: 'pointer',
                                                color: 'red',
                                                marginLeft: '10px',
                                                fontSize: '1.2rem',
                                            }}
                                            title="Remove Stroke"
                                        >
                                            ðŸš«
                                        </span>
                                    </td>
                                    <td style={{ padding: '10px', border: '1px solid #ccc', whiteSpace: 'nowrap' }}>
                                        {stroke || 'Unassigned'}
                                    </td>
                                    <td style={{ padding: '10px', border: '1px solid #ccc', whiteSpace: 'nowrap' }}>
                                        <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                                            {players.map((playerName: string) => (
                                                <button
                                                    key={playerName}
                                                    onMouseDown={() => togglePlayerSelection(playerName)} // Handle press and hold
                                                    onMouseUp={() => assignPlayersToStroke(index)} // Assign players on release
                                                    style={{
                                                        padding: '10px',
                                                        backgroundColor: selectedPlayers.includes(playerName)
                                                            ? '#4caf50'
                                                            : '#1976d2',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '5px',
                                                        cursor: 'pointer',
                                                        textAlign: 'center',
                                                    }}
                                                >
                                                    {playerName}
                                                </button>
                                            ))}
                                        </div>
                                    </td>
                                    <td style={{ padding: '10px', border: '1px solid #ccc', whiteSpace: 'nowrap' }}>
                                        <select
                                            value={shotTypes[currentHole - 1]?.[index] || ''}
                                            onChange={(e) => selectShotType(index, e.target.value)}
                                            style={{
                                                padding: '5px',
                                                fontSize: '14px',
                                                borderRadius: '5px',
                                                border: '1px solid #ccc',
                                                maxWidth: '150px',
                                            }}
                                        >
                                            <option value="" disabled>
                                                Select Shot Type
                                            </option>
                                            {shotTypeOptions.map((option) => (
                                                <option key={option} value={option}>
                                                    {option}
                                                </option>
                                            ))}
                                        </select>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan={4} style={{ textAlign: 'center', padding: '10px' }}>
                                    No strokes added for this hole.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
                <button
                    onClick={addStroke}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#1976d2',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        marginRight: '10px',
                    }}
                >
                    Add Stroke
                </button>
                <button
                    onClick={() => setCurrentHole((prev) => Math.max(prev - 1, 1))} // Go to the previous hole
                    disabled={currentHole === 1}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: currentHole === 1 ? '#ccc' : '#1976d2',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: currentHole === 1 ? 'not-allowed' : 'pointer',
                        marginRight: '10px',
                    }}
                >
                    Back
                </button>
                <button
                    onClick={() => setCurrentHole((prev) => Math.min(prev + 1, 18))} // Go to the next hole
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#1976d2',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        marginRight: '10px',
                    }}
                >
                    Next Hole
                </button>
                <button
                    onClick={() => {
                        if (goToLeaderboard) {
                            goToLeaderboard();
                        } else {
                            console.error('goToLeaderboard function is not defined.');
                        }
                    }}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#1976d2',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                    }}
                >
                    Leaderboard
                </button>
            </div>
        </div>
    );
};

export default Game;
